CustomField.formula:
IF(
  AND(NOT(ISBLANK(Latest_Opp_Created__c)), ISBLANK(Latest_Case_Created__c)),
  /* Only Opp exists */
  TEXT(Latest_Opp_Created__r.StageName) & " - " & TEXT(Latest_Opp_Created__r.Sub_Stage__c),

  IF(
    AND(ISBLANK(Latest_Opp_Created__c), NOT(ISBLANK(Latest_Case_Created__c))),
    /* Only Case exists */
    TEXT(Latest_Case_Created__r.Status) & " - " & TEXT(Latest_Case_Created__r.Sub_status__c),

    IF(
      AND(NOT(ISBLANK(Latest_Opp_Created__c)), NOT(ISBLANK(Latest_Case_Created__c))),
      /* Both exist: pick the newer */
      IF(
        Latest_Opp_Created__r.CreatedDate > Latest_Case_Created__r.CreatedDate,
        TEXT(Latest_Opp_Created__r.StageName) & " - " & TEXT(Latest_Opp_Created__r.Sub_Stage__c),
        TEXT(Latest_Case_Created__r.Status) & " - " & TEXT(Latest_Case_Created__r.Sub_status__c)
      ),
      /* Neither exists */
      ""
    )
  )
)
